mapper.select:
SELECT id, name, description, price, stock_quantity, status, created_at, updated_at
FROM products
WHERE id = #{id}

SELECT id, name, description, price, stock_quantity, status, created_at, updated_at
FROM products
WHERE category_id = #{categoryId}
  AND name LIKE CONCAT('%', #{keyword}, '%')
  AND price BETWEEN #{minPrice} AND #{maxPrice}
ORDER BY id DESC

SELECT id, name, description, price, stock_quantity, status, created_at, updated_at
FROM products
WHERE category_id = #{categoryId}
  AND price BETWEEN #{minPrice} AND #{maxPrice}
ORDER BY id DESC
